/*-------------------------------------------------------------------------*/


#include <config.h>

#include "CubeSerializable.h"

#include "CubeConnection.h"

using namespace cube;

namespace cube
{
// --- Serialization ------------------------------------------------------

void
Serializable::serialize( Connection& connection ) const
{
    connection << this->get_serialization_key();
    pack( connection );
}
}    // namespace cube
